(39, 'easilifestyle', 'easilifestyle', '', '<h1><img src="images/stories/easilifes.jpg" border="0" width="159" style="float: left; margin-right: 5px; margin-left: 5px;" /><a class="showcase" href="index.php?option=com_content&view=article&id=48&Itemid=13" style="text-decoration:none;color:#1C426F;padding-left:3px;"><strong>EasilifeStyle </strong></a></h1>\r\n<p style="text-align: justify;padding-left:173px;"><span style="color: #000000;"><span style="font-family:arial,helvetica,sans-serif;"><span style="font-size: 12px;">The idea of EasilifeStyle  comes naturally – a revolutionary online shopping experience for health, wellness, lifestyle and personal care products and services around the clock. It’s convenient; it’s money and time saving for you and your family; it’s rewarding and it’s for everyone</span></span></span></p>', '', 0, 6, 0, 6, '2011-10-31 09:32:21', 62, '', '2011-11-17 03:07:29', 62, 0, '0000-00-00 00:00:00', '2011-10-31 09:32:21', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 10, 0, 5, '', '', 0, 19, 'robots=\nauthor='),
(40, 'umishushi', 'umishushi', '', '<h1><img src="images/stories/umishushi.jpg" border="0" style="float: left; margin: 5px 10px;" /><a class="showcase" href="index.php?option=com_content&view=article&id=46&Itemid=13" style="text-decoration:none;color:#1C426F;"><strong>Umisushi</strong></a></h1>\r\n<p style="text-align: justify;"><span style="font-family:arial,helvetica,sans-serif;padding-right:10px;"><span style="font-size: 12px;">The idea of umi was borne over a meal of sushi amongst a group of friends and relatives. With this desire, we embarked on an exciting gastronomical journey to learn everything about this popular Japanese cuisine.</span></span></p>', '', 0, 6, 0, 6, '2011-10-31 09:36:56', 62, '', '2011-11-17 02:47:02', 62, 0, '0000-00-00 00:00:00', '2011-10-31 09:36:56', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 11, 0, 4, '', '', 0, 15, 'robots=\nauthor='),
(41, 'selvni', 'selvni', '', '<p><img src="images/stories/selvni.jpg" border="0" width="159" style="float: left; margin-left: 5px; margin-right: 5px;" /></p>\r\n<h1><a class="showcase" href="index.php?option=com_content&view=article&id=49&Itemid=13" style="text-decoration:none;color:#1C426F;padding-left:3px;"><strong>Selvni Pheli</strong></a></h1>\r\n<p style="text-align: justify;padding-left:173px;"><span style="font-family:arial,helvetica,sans-serif;"><span style="font-size: 12px;">We are Halal Certified, and designing custom-orders of vegetarian pastries, cakes and cookies for weddings, birthdays, special occasions and anniversaries is our forte and specialty. </span></span></p>', '', 0, 6, 0, 6, '2011-10-31 09:41:30', 62, '', '2011-11-17 03:08:26', 62, 0, '0000-00-00 00:00:00', '2011-10-31 09:41:30', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 11, 0, 3, '', '', 0, 16, 'robots=\nauthor='),
